pragma solidity >=0.4.0 <0.6.0;

contract Book {

    address public author;
    address public publisher;
    string public ipfsHash;
    string public title;
    uint public dateOfPublishing;

    //modifiers
    modifier  OnlyAuthor() {
        require(msg.sender == author, 'You are not the author :(');
        _;
    }

    constructor(string memory _title, string memory _ipfsHash) public {
        title = _title;
        ipfsHash = _ipfsHash;
        author = msg.sender;
    }

    function setPublisher(address _publisher) OnlyAuthor public {
        publisher = _publisher;
        dateOfPublishing = now;
    }

    function getBookDetails() public returns (string memory _title , string memory _ipfsHash) {
        return(title , ipfsHash);
    }


}
